import javax.swing.*;
public class Increment {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
int c;
c=5;
JOptionPane.showMessageDialog(null,c);
JOptionPane.showMessageDialog(null,c++);
JOptionPane.showMessageDialog(null,c);
c=6;
JOptionPane.showMessageDialog(null,c);
JOptionPane.showMessageDialog(null,++c);
JOptionPane.showMessageDialog(null,c);
	}

}
